import re
from bs4 import BeautifulSoup

def decode_tesseract_xml(xml_content):
    soup = BeautifulSoup(xml_content, "xml")
    words = soup.find_all('span', {'class' : 'ocrx_word'})

    coordinates = []
    text = []
    
    for word in words:
        title = word['title']
        bbox = re.search(r'bbox ((\d+ \d+ \d+ \d+))', title).group(1)
        coordinates.append(bbox)
        
        word_text = word.get_text()
        text.append(word_text)

    return coordinates, text

